mapper.delete:
update page
SET rowStatus = 0
WHERE id = #{id}
 AND createBy = #{createBy}

mapper.update:
update page


        `name` = #{name},


        pageSoul = #{pageSoul},


        `appId` = #{appId},

    updateBy = #{updateBy},
    updateDt = #{updateDt}

WHERE id = #{id}
AND createBy = #{createBy}

mapper.select:
select `name`,appId,createBy,createDt,updateBy,updateDt,rowStatus
from page


       id = #{id}


        and `name`= #{name}

    and rowStatus = 1

ORDER  BY id DESC
